package com.sell.aspect;import lombok.extern.slf4j.Slf4j;import org.aspectj.lang.annotation.After;import org.aspectj.lang.annotation.Aspect;import org.aspectj.lang.annotation.Before;import org.aspectj.lang.annotation.Pointcut;import org.springframework.stereotype.Component;@Slf4j@Aspect@Componentpublic class AuthCheckController {    @Before("init()")    public void httpLog(){        log.info("拦截HTTPS");    }    @After("init()")    public void auth(){        log.info("拦截返回的日志");    }    /**     * 拦截某个包下的所有类的方法     */    @Pointcut("execution(public * com.sell.controller.SellOrderController.*(..))")    public void init(){    }}