package com.sell.controller;import com.sell.properties.UrlProperties;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;@Slf4j@Controller@RequestMapping("/test")public class TestController {    @Autowired    private UrlProperties urlPropertier;    @ResponseBody    @GetMapping("/config")    public String testConfig(){        return urlPropertier.getQiniuUrl();    }    @ResponseBody    @GetMapping(value = "/config/{id}")    public Integer testGetParam(@PathVariable("id") Integer id){        return id;    }    @ResponseBody    @GetMapping(value = "/user")    public Integer testGetParam1(@RequestParam("id") Integer id){        return id;    }}