package com.sell.dataobject.mapper;import com.sell.dataobject.ProductCategory;import org.apache.ibatis.annotations.Insert;import org.apache.ibatis.annotations.Result;import org.apache.ibatis.annotations.Results;import org.apache.ibatis.annotations.Select;import java.util.List;import java.util.Map;public interface ProductCategoryMapper {    @Insert("insert into product_category(category_name,category_type) values(#{category_name,jdbcType=VARCHAR},#{category_type,jdbcType=INTEGER})")    Integer insertByMapper(Map<String,Object> map);    @Insert("insert into product_category(category_name,category_type) values(#{categoryName,jdbcType=VARCHAR},#{categoryType,jdbcType=INTEGER})")    Integer insertByObject(ProductCategory productCategory);    @Select("select * from product_category where category_id = #{categoryId,jdbcType=INTEGER}")    @Results({            @Result(column = "category_id",property = "categoryId"),            @Result(column = "category_name",property = "categoryName"),            @Result(column = "category_type",property = "categoryType")    })    ProductCategory findById(Integer categoryId);    @Select("select * from product_category")    @Results({            @Result(column = "category_id",property = "categoryId"),            @Result(column = "category_name",property = "categoryName"),            @Result(column = "category_type",property = "categoryType")    })    List<ProductCategory> findAll();    ProductCategory selectByCategoryType(Integer categoryType);    List<ProductCategory> selectAll();}