package com.sell.exception;import com.sell.enums.ResponseEnum;import lombok.Data;/** * 自定义异常，继承runtimeException,spring只对runtimeException进行事物回滚 */@Datapublic class ApiException extends RuntimeException {    private Integer code;    public ApiException(ResponseEnum responseEnum){        super(responseEnum.getMessage());        this.code = responseEnum.getCode();    }    public ApiException(Integer code,String message){        super(message);        this.code = code;    }}