package com.sell.exception;import com.sell.responseUntils.ResponseResult;import com.sell.responseobject.Response;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.ResponseBody;/** * 捕获异常进行处理 */@Slf4j@ControllerAdvicepublic class ExceptionHandle {    /**     * 捕获异常，并处理相关异常     * @param e     * @return     */    @ResponseBody    @ExceptionHandler(value = Exception.class)    public Response handle(Exception e){        if(e instanceof ApiException){            ApiException apiException = (ApiException) e;            return ResponseResult.fail(apiException.getCode(),apiException.getMessage());        }else{            log.error("【异常】"+e);            return ResponseResult.fail(-1,"未知错误");        }    }}