package com.sell.exception;import com.sell.enums.ResponseEnum;import lombok.Data;/** * 自定义异常 */@Datapublic class SellException extends RuntimeException {    private Integer code;     public SellException(ResponseEnum responseEnum){        super(responseEnum.getMessage());        this.code = responseEnum.getCode();     }     public SellException(Integer code,String message){         super(message);         this.code = code;     }}