package com.repository;import com.sell.dataobject.OrderDetail;import com.sell.repository.OrderDetailRepository;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.math.BigDecimal;import java.util.List;@RunWith(SpringRunner.class)@SpringBootTestpublic class OrderDetailRepositoryTest {    @Autowired    private OrderDetailRepository repository;    @Test    public void save(){        OrderDetail orderDetail = new OrderDetail();        orderDetail.setDetailId("0002");        orderDetail.setOrderId("0001");        orderDetail.setProductName("华为");        orderDetail.setProductPrice(new BigDecimal(3000));        orderDetail.setProductIcon("http://baidu.com");        orderDetail.setProductQuantity(100);        orderDetail.setProductId("0002");        OrderDetail result = repository.save(orderDetail);        Assert.assertEquals(orderDetail.getProductName(),result.getProductName());    }    @Test    public void findByOrderId() {        List<OrderDetail> orderDetails = repository.findByOrderId("0001");        Assert.assertNotNull(orderDetails);    }}