package com.repository;import com.sell.dataobject.OrderMaster;import com.sell.repository.OrderMasterRepository;import org.junit.Assert;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.data.domain.Page;import org.springframework.data.domain.PageRequest;import org.springframework.test.context.junit4.SpringRunner;import javax.transaction.Transactional;import java.math.BigDecimal;@RunWith(SpringRunner.class)@SpringBootTestpublic class OrderMasterRepositoryTest {    @Autowired    private OrderMasterRepository repository;    @Test    @Transactional    public void save(){        OrderMaster orderMaster = new OrderMaster();        orderMaster.setOrderId("0001");        orderMaster.setBuyerName("叶子");        orderMaster.setBuyerPhone("13425244866");        orderMaster.setBuyerAddress("中国");        orderMaster.setBuyerOpenid("sdfsdfdsfgdfsdf4w5rwge23sdf");        orderMaster.setOrderAmount(new BigDecimal(100));        OrderMaster result = repository.save(orderMaster);        Assert.assertEquals(orderMaster.getBuyerName(),result.getBuyerName());    }    @Test    public void findByOrderId() {        OrderMaster orderMaster = repository.findByOrderId("0001");        Assert.assertNotNull(orderMaster);    }    @Test    public void findBYOpenId(){        PageRequest request = new PageRequest(0,5);        Page<OrderMaster> orderMaster = repository.findByBuyerOpenid("sdfsdfdsfgdfsdf4w5rwge23sdf",request);        Assert.assertNotNull(orderMaster);    }}